import React, { Component } from 'react';
import Pusher from 'pusher-js';

class Stall extends Component {
  constructor(props) {
    super(props);
    this.pusher = props.pusher;
    this.presenceChannel = null;
    this.state = {
      occupants: {count: 0},
    };
    this.max_occupancy = 4; // ADJUST AS NEEDED
  }

  componentDidMount() {
    this.presenceChannel = this.pusher.subscribe('stall');
    this.presenceChannel.bind('pusher:subscription_succeeded', () => {
      this.updateOccupants(this.presenceChannel.members);
      console.log(this.presenceChannel.members.me.id + ' joined Stall');
    });
    this.presenceChannel.bind('pusher:member_added', () => {
      this.updateOccupants(this.presenceChannel.members);
    });
    this.presenceChannel.bind('pusher:member_removed', () => {
      this.updateOccupants(this.presenceChannel.members);
      console.log('someone left Stall');
    });
  }

  handleOccupantsChange(e) {
    this.props.onVacancyChange(e.target.value);
  }

  updateOccupants(member) {
    this.setState({
      visitors: [...this.state.occupants, member],
    })
  }

  render() {
    const vacant = this.props.vacant;
    return (
      <div id="stall">
        Stall: {this.state.occupants.count} / {this.max_occupancy}
      </div>
    );
  }

}

export default Stall;
