import React, { Component } from 'react';
import Pusher from 'pusher-js';
import Stall from './Stall';
import EnterStall from './EnterStall';

class WaitingRoom extends Component {
  constructor(props) {
    super(props);
    this.pusher, this.presenceChannel;
    this.state = {
      visitors: [],
    };
    this.handleVacancyChange = this.handleVacancyChange.bind(this);
    this.handleEnterStallClick = this.handleEnterStallClick.bind(this);
  }

  handleVacancyChange() {
    console.log('vacant stall found');
  }

  handleEnterStallClick() {
    console.log('entering stall');
  }

  componentDidMount() {
    this.pusher = new Pusher('93d5b6db6095187f5ef6', {
      cluster: 'us2',
      encrypted: true,
    });

    this.presenceChannel = this.pusher.subscribe('presence-bathroomQueue');
    this.presenceChannel.bind('pusher:subscription_succeeded', () => {
      this.updateVisitors(presenceChannel.members);
      console.log('joined WaitingRoom');
    });
    this.presenceChannel.bind('pusher:member_added', () => {
      this.updateVisitors(presenceChannel.members);
    });
    this.presenceChannel.bind('pusher:member_removed', () => {
      this.updateVisitors(presenceChannel.members);
      console.log('someone left WaitingRoom');
    });
  }

  componentWillUnmount() {
    this.presenceChannel.unbind();
    this.pusher.unsubscribe(this.presenceChannel);
  }

  updateVisitors(members) {
    this.setState({
      visitors: members,
    })
  }

  render() {
    const vacant = 4;
    let enterStallButton;
    if (vacant > 0) {
      enterStallButton = <EnterStall onClick={this.handleEnterStallClick} />;
    }

    return (
      <div id="users">
        Currently online: {this.state.visitors.count}
        <br/>
        {enterStallButton}
        <Stall
          vacant={vacant}
          onVacancyChange={this.handleVacancyChange} />
      </div>
    );
  }

}

export default WaitingRoom;
