import React, { Component } from 'react';
import Pusher from 'pusher-js';
import WaitingRoom from './WaitingRoom';
import Stall from './Stall';

class App extends Component {
  constructor(props) {
    super(props);
    this.pusher = new Pusher('93d5b6db6095187f5ef6', {
      cluster: 'us2',
      encrypted: true,
    });
    this.state = {
      currentView: null,
      stalls: [],
    };
    this.max_occupancy = 4; // ADJUST AS NEEDED
    this.handleEnterStall = this.handleEnterStall.bind(this);
    // this.handleVacancyChange = this.handleVacancyChange.bind(this);
  }

  handleEnterStall(e) {
    var stallComponents = Array.from(this.state.stalls);
    var stallId = stallComponents.length + 1;
    var newStall = <Stall id={stallId} pusher={this.pusher} max={this.max_occupancy} />;
    stallComponents.push(newStall);
    console.log(stallComponents);
    this.setState(currentState => {
      return {
        currentView: newStall,
        stalls: stallComponents,
      };
    });
    // this.stallChannel = this.pusher.subscribe('presence-stall-1');
    // this.stallChannel.bind('pusher:subscription_succeeded', () => {
    //   console.log(this.stallChannel.members.me.id + ' joined stall 1');
    //   this.setState({
    //     stalls: {'1': this.stallChannel.members},
    //   },
    //   console.log(this.stallChannel.members));
    // });
  }

  componentWillMount() {
    this.setState(currentState => {
      return {currentView: <WaitingRoom onEnterStall={this.handleEnterStall} pusher={this.pusher} />};
    });
  }

  // componentDidMount() {
  // }

  // componentWillUnmount() {
  // }

  // updateVisitors(members) {
  //   this.setState({
  //     visitors: members,
  //   },
  //   // console.log('number of visitors updated')
  //   )
  // }

  render() {
    return (
      <div id="app">
        {this.state.currentView}
      </div>
    );
  }

}

export default App;
