import React, { Component } from 'react';
import Pusher from 'pusher-js';
import axios from 'axios';
// import Stall from './Stall';
import EnterStall from './EnterStall';

class App extends Component {
  constructor(props) {
    super(props);
    this.pusher = new Pusher('93d5b6db6095187f5ef6', {
      cluster: 'us2',
      encrypted: true,
    });
    this.presenceChannel = null;
    this.stallChannel = null;
    this.state = {
      visitors: [],
      stalls: {
        '1': {count: 0},
      }
    };
    this.handleVacancyChange = this.handleVacancyChange.bind(this);
    this.handleEnterStallClick = this.handleEnterStallClick.bind(this);
  }

  handleVacancyChange() {
    console.log('vacant stall found');
  }

  handleEnterStallClick() {
    console.log('entering stall');
    this.stallChannel = this.pusher.subscribe('presence-stall-1');
    this.stallChannel.bind('pusher:subscription_succeeded', () => {
      console.log(this.stallChannel.members.me.id + ' joined stall 1');
      this.setState({
        stalls: {'1': this.stallChannel.members},
      },
      console.log(this.stallChannel.members));
    });
  }

  componentDidMount() {
    this.presenceChannel = this.pusher.subscribe('presence-bathroom');
    this.presenceChannel.bind('pusher:subscription_succeeded', () => {
      this.updateVisitors(this.presenceChannel.members);
      console.log(this.presenceChannel.members.me.id + ' joined WaitingRoom');
    });
    this.presenceChannel.bind('pusher:member_added', () => {
      this.updateVisitors(this.presenceChannel.members);
    });
    this.presenceChannel.bind('pusher:member_removed', () => {
      this.updateVisitors(this.presenceChannel.members);
      // console.log('someone left WaitingRoom');
    });

    axios.get('http://localhost:3000').then(({ data }) => {
      this.setState({
        comments: [...data],
      });
    }).catch(error => console.log(error))    

    this.presenceChannel.bind('enter-stall', () => {
      // see Comments.js and copy structure for communicating with server through POST
    })
  }

  componentWillUnmount() {
    this.presenceChannel.unbind(); // remove all handlers defined in DidMount()
    this.pusher.unsubscribe(this.presenceChannel);

  }

  updateVisitors(members) {
    this.setState({
      visitors: members,
    },
    // console.log('number of visitors updated')
    )
  }

  render() {
    const vacant = 4;
    let enterStallButton;
    if (vacant > 0) {
      enterStallButton = <EnterStall onClick={this.handleEnterStallClick} />;
    }

    return (
      <div id="app">
        Currently online (total): {this.state.visitors.count}<br/>
        In line: {this.state.visitors.count - this.state.stalls['1'].count}<br/>
        Stall: {this.state.stalls['1'].count} / 4
        <br/>
        {enterStallButton}
      </div>
    );
  }

}

export default App;
